<?php

namespace AppBundle\Repository\Offerpage\Offer\Item;

use AppBundle\Repository\Repository;

/**
 * SiteRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class ItemRepository extends Repository{
	
	public function getPaginated($limit, $offset, $filters = [], $path=""){
		$data = $this->getEntityManager()->createQueryBuilder();
		$data->from('AppBundle\Entity\Offerpage\Offer\Item\Item', 'i');
		$data->where('i.offer = :id');
		$data->setParameter('id' , $filters['id'] );
		$data->andWhere('i.type = :type');
		$data->setParameter('type' , $filters['type'] );
	
		
	
		$count = clone $data;
		$count->select('count(i.id)');
		$total = $count->getQuery()->getSingleScalarResult();
	
		$data->select('i');
		if(!is_null($offset) && !is_null($limit)){
			$data->setFirstResult($offset);
			$data->setMaxResults($limit);
		}
		$result = $data->getQuery()->getResult();
	
		$final = [];
		foreach($result as $item){
			$result_offer = [];
			$result_offer['id'] = $item->getId();
			$result_offer['title'] = $item->getTitle();
			$result_offer['url'] = $path.$item->getWebPath();
			$result_offer['featured'] = $item->getFeatured();
			$final[] = $result_offer;
		}

		if(!is_null($offset) && !is_null($limit)){
			return $this->payload($total, $limit, $offset, $final);
		} else {
			return $final;
		}
	
	}
	
	public function clearFeaturedItems($offer_id){
	
		$data = $this->getEntityManager()->createQueryBuilder();
			$q = $data->update( 'AppBundle\Entity\Offerpage\Offer\Item\Item' ,'i')
			->set('i.featured', '?1')
			->where('i.offer = :offer_id')
			->setParameter('1', false)
			->setParameter('offer_id', $offer_id)
			->getQuery();
			$p = $q->execute();
	}
}
